document.addEventListener('DOMContentLoaded',function(){const mobileMenuToggle=document.querySelector('.mobile-menu-toggle');const mainMenu=document.querySelector('.main-menu');const menuOverlay=document.createElement('div');menuOverlay.className='menu-overlay';document.body.appendChild(menuOverlay);if(mobileMenuToggle){mobileMenuToggle.addEventListener('click',function(){mainMenu.classList.toggle('active');menuOverlay.classList.toggle('active');document.body.classList.toggle('menu-open')});}if(menuOverlay){menuOverlay.addEventListener('click',function(){mainMenu.classList.remove('active');menuOverlay.classList.remove('active');document.body.classList.remove('menu-open')});}const header=document.querySelector('header');if(header){const currentPage=window.location.pathname.split('/').pop();const navLinks=document.querySelectorAll('.main-menu > li > a');navLinks.forEach(link=>{const linkPage=link.getAttribute('href');if(linkPage===currentPage||currentPage===''&&linkPage==='index.html'){link.parentElement.classList.add('active');}});window.addEventListener('scroll',function(){if(window.scrollY>80){header.classList.add('scrolled');}else{header.classList.remove('scrolled');}});}const testimonialDots=document.querySelectorAll('.testimonial-dot');const testimonialSlides=document.querySelectorAll('.testimonial-slide');if(testimonialDots.length>0&&testimonialSlides.length>0){testimonialDots.forEach((dot,index)=>{dot.addEventListener('click',()=>{testimonialSlides.forEach(slide=>slide.classList.remove('active'));testimonialDots.forEach(dot=>dot.classList.remove('active'));testimonialSlides[index].classList.add('active');dot.classList.add('active');});});}});
